tables.table_schema:
CREATE TABLE UserActivity (
  user_id INT PRIMARY KEY,
  app_id INT,
  time_stamp TIMESTAMP,
  duration_minutes INT
);

CREATE TABLE app_sessions (
   session_id SERIAL PRIMARY KEY,
   user_id INT REFERENCES app_users(user_id),
   session_duration INTERVAL,
   session_date DATE
);

CREATE TABLE ad_impressions (
   impression_id SERIAL PRIMARY KEY,
   user_id INT REFERENCES app_users(user_id),
   ad_id INT,
   impression_date DATE
);

CREATE TABLE ad_clicks (
   click_id SERIAL PRIMARY KEY,
   impression_id INT REFERENCES ad_impressions(impression_id),
   click_date TIMESTAMP
);

CREATE TABLE User_Ad_Interactions (
    user_id UUID REFERENCES User(user_id),
    ad_id UUID REFERENCES Advertisements(ad_id),
    interaction_type VARCHAR(20) NOT NULL,
    interaction_date DATE NOT NULL
);
