tables.table_schema:
CREATE TABLE user_interactions ( 
  user_id INT PRIMARY KEY, 
  ad_id INT, 
  interaction_timestamp TIMESTAMP, 
  interaction_type VARCHAR(50) 
);

CREATE TABLE ad_targeting_algorithms ( 
  algorithm_id INT PRIMARY KEY, 
  algorithm_name VARCHAR(50), 
  description TEXT, 
  last_updated TIMESTAMP 
);

CREATE TABLE ad_targeting (
    targeting_id INT PRIMARY KEY,
    ad_id INT NOT NULL,
    user_id INT NOT NULL,
    targeting_score FLOAT
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    user_age INT,
    user_location VARCHAR(100)
);

CREATE TABLE Ads (
    ad_id INT PRIMARY KEY,
    ad_name VARCHAR(50),
    ad_type VARCHAR(20),
    ad_target_group VARCHAR(50)
);

CREATE TABLE AdTargetingMetrics (
    metric_id INT PRIMARY KEY,
    ad_id INT,
    targeting_metric VARCHAR(50),
    metric_value FLOAT
);

CREATE TABLE user_interaction (
    user_id UUID PRIMARY KEY,
    ad_id UUID,
    interaction_timestamp TIMESTAMP,
    interaction_type VARCHAR
);

CREATE TABLE ad_targeting (
    ad_id UUID PRIMARY KEY,
    target_audience VARCHAR,
    targeting_criteria JSONB
);

CREATE TABLE ad_impressions (
    impression_id UUID PRIMARY KEY,
    ad_id UUID,
    user_id UUID,
    impression_timestamp TIMESTAMP
);

CREATE TABLE ad_clicks (
    click_id UUID PRIMARY KEY,
    ad_id UUID,
    user_id UUID,
    click_timestamp TIMESTAMP
);

CREATE TABLE ad_conversion (
    conversion_id UUID PRIMARY KEY,
    ad_id UUID,
    user_id UUID,
    conversion_timestamp TIMESTAMP
);
