tables.table_schema:
CREATE TABLE Audience (
    audience_id INT PRIMARY KEY,
    age INT,
    gender VARCHAR(10),
    location VARCHAR(50)
);

CREATE TABLE Videos (
    video_id INT PRIMARY KEY,
    title VARCHAR(100),
    genre VARCHAR(50),
    duration INT
);

CREATE TABLE Recommendations (
    recommendation_id INT PRIMARY KEY,
    audience_id INT,
    video_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE audience_segment (
   segment_id INT PRIMARY KEY,
   segment_name VARCHAR(255),
   segment_description TEXT
);

CREATE TABLE recommendation_system (
   recommendation_id INT PRIMARY KEY,
   user_id INT,
   recommended_video_id INT,
   timestamp TIMESTAMP
);
