tables.table_schema:
CREATE TABLE viewer_behavior_tracking (
    viewer_id UUID PRIMARY KEY,
    timestamp TIMESTAMPTZ,
    content_id UUID,
    action_type TEXT
);

CREATE TABLE viewer_behavior (
    viewer_id UUID PRIMARY KEY,
    content_id UUID,
    action_type VARCHAR,
    action_timestamp TIMESTAMP
);

CREATE TABLE content_recommendations (
    content_id UUID PRIMARY KEY,
    recommended_content_id UUID,
    recommendation_score FLOAT,
    recommendation_timestamp TIMESTAMP
);

CREATE TABLE viewer_behavior (
    viewer_id INT PRIMARY KEY,
    content_id INT,
    behavior_type VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE content (
    content_id INT PRIMARY KEY,
    title VARCHAR(100),
    category VARCHAR(50),
    rating DECIMAL(3,1)
);

CREATE TABLE viewers (
 viewer_id INT PRIMARY KEY,
 name VARCHAR(50) NOT NULL,
 age INT,
 gender VARCHAR(10)
);

CREATE TABLE videos (
 video_id INT PRIMARY KEY,
 title VARCHAR(100) NOT NULL,
 length INT,
 category VARCHAR(50)
);

CREATE TABLE recommendations (
 recommendation_id INT PRIMARY KEY,
 viewer_id INT,
 video_id INT,
 recommendation_score FLOAT
);
