tables.table_schema:
CREATE TABLE user_consent (
    user_id UUID PRIMARY KEY,
    consent_given BOOLEAN,
    data_processing_allowed BOOLEAN,
    data_sharing_allowed BOOLEAN
);

CREATE TABLE consent_logs (
    log_id SERIAL PRIMARY KEY,
    user_id UUID REFERENCES user_consent(user_id),
    action_type VARCHAR(50),
    timestamp TIMESTAMPTZ
);

CREATE TABLE user_consent (
    user_id INT,
    consent_date DATE,
    data_processing_consent BOOL,
    data_sharing_consent BOOL
);

CREATE TABLE consent_history (
    record_id INT,
    user_id INT,
    action_date DATE,
    action_type ENUM('Data Processing Consent', 'Data Sharing Consent')
);

CREATE TABLE privacy_regulations (
    regulation_id INT,
    regulation_name VARCHAR(50),
    description TEXT
);

CREATE TABLE user_permissions (
 user_id INT PRIMARY KEY,
 consent_given BOOL,
 consent_last_updated TIMESTAMP
);

CREATE TABLE data_collection (
 data_id INT PRIMARY KEY,
 user_id INT,
 data_type VARCHAR(255),
 data_collected_date TIMESTAMP
);

CREATE TABLE data_sharing (
 data_id INT PRIMARY KEY,
 shared_with VARCHAR(255),
 sharing_date TIMESTAMP
);

CREATE TABLE privacy_regulations (
 regulation_id INT PRIMARY KEY,
 regulation_name VARCHAR(255),
 regulation_description TEXT
);

CREATE TABLE user_consent (
    user_id INT PRIMARY KEY,
    consent_option VARCHAR(50),
    consent_date TIMESTAMP,
    consent_status BOOLEAN
);

CREATE TABLE data_sharing (
    share_id INT PRIMARY KEY,
    shared_data TEXT,
    recipient_id INT,
    sharing_date TIMESTAMP
);

CREATE TABLE regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    regulation_description TEXT
);

CREATE TABLE user_profile (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(100),
    user_email VARCHAR(100),
    user_address TEXT
);
