tables.table_schema:
CREATE TABLE reviewers (
    reviewer_id INT PRIMARY KEY,
    reviewer_name VARCHAR(100),
    reviewer_title VARCHAR(100),
    reviewer_department VARCHAR(100)
);

CREATE TABLE regulatory_reports (
  report_id UUID PRIMARY KEY,
  report_name VARCHAR(255),
  report_date DATE,
  report_status VARCHAR(50)
);

CREATE TABLE regulatory_users (
  user_id UUID PRIMARY KEY,
  user_name VARCHAR(50),
  user_role VARCHAR(50),
  user_email VARCHAR(100)
);

CREATE TABLE regulatory_compliance_reports (
	rcr_id SERIAL PRIMARY KEY,
	rcr_name VARCHAR(50) NOT NULL,
	rcr_type VARCHAR(50) NOT NULL,
	rcr_date DATE NOT NULL
);

CREATE TABLE compliance_criteria (
	cc_id SERIAL PRIMARY KEY,
	cc_name VARCHAR(50) NOT NULL,
	cc_description TEXT NOT NULL
);

CREATE TABLE report_results (
	rr_id SERIAL PRIMARY KEY,
	rcr_id INT NOT NULL,
	cc_id INT NOT NULL,
	rr_status VARCHAR(20) NOT NULL
);

CREATE TABLE users (
	user_id SERIAL PRIMARY KEY,
	username VARCHAR(50) NOT NULL,
	password VARCHAR(50) NOT NULL
);

CREATE TABLE regulatory_reports (
	rr_id INT PRIMARY KEY,
	report_name VARCHAR(100),
	report_date DATE,
	report_description TEXT
);

CREATE TABLE compliance_checks (
	cc_id INT PRIMARY KEY,
	check_date DATE,
	check_pass BOOLEAN,
	rr_id INT
);

CREATE TABLE regulations (
	reg_id INT PRIMARY KEY,
	reg_name VARCHAR(100),
	reg_description TEXT
);

CREATE TABLE regulatory_agencies (
	ra_id INT PRIMARY KEY,
	ra_name VARCHAR(100),
	ra_contact_person VARCHAR(100),
	ra_email VARCHAR(100)
);

CREATE TABLE compliance_history (
	ch_id INT PRIMARY KEY,
	rr_id INT,
	prev_check_date DATE,
	compliance_status VARCHAR(50)
);
