tables.table_schema:
CREATE TABLE regulatory_data (
    id SERIAL PRIMARY KEY,
    data_entry TEXT NOT NULL,
    regulatory_trigger BOOLEAN
);

CREATE TABLE regulatory_logs (
    log_id SERIAL PRIMARY KEY,
    data_id INT REFERENCES regulatory_data(id),
    log_time TIMESTAMPTZ DEFAULT NOW()
);

CREATE TABLE regulatory_requirements (
    id INT PRIMARY KEY,
    description TEXT
);

CREATE TABLE triggers (
    id INT PRIMARY KEY,
    trigger_name TEXT,
    operation_type TEXT
);

CREATE TABLE data_updates (
    id INT PRIMARY KEY,
    data_type TEXT,
    update_time TIMESTAMP
);

CREATE TABLE table1 (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    age INT
);

CREATE TABLE table2 (
    id INT PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    price DECIMAL(10, 2) NOT NULL,
    quantity INT
);

CREATE TABLE table3 (
    id INT PRIMARY KEY,
    user_id INT NOT NULL,
    action VARCHAR(255) NOT NULL,
    timestamp TIMESTAMP
);

CREATE TABLE table4 (
    id INT PRIMARY KEY,
    account_number VARCHAR(20) NOT NULL,
    balance DECIMAL(12, 2) NOT NULL
);

CREATE TABLE customers (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  address VARCHAR(100),
  created_at TIMESTAMP
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  price DECIMAL(10,2) NOT NULL,
  stock_quantity INT
);
