tables.table_schema:
CREATE TABLE regulatory_compliance_assessments (
  assessment_id INT PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  assessment_date DATE,
  compliance_rating ENUM('Exceptional', 'Good', 'Average', 'Below Average')
);

CREATE TABLE regulatory_compliance_assessments (
    assessment_id INT PRIMARY KEY,
    assessment_name VARCHAR(255),
    assessment_status VARCHAR(50),
    assessment_date DATE
);

CREATE TABLE regulatory_assessments (
   assessment_id SERIAL PRIMARY KEY,
   assessment_name VARCHAR(255) NOT NULL,
   assessment_date DATE,
   regulatory_requirement TEXT
);

CREATE TABLE compliance_tasks (
   task_id SERIAL PRIMARY KEY,
   task_name VARCHAR(255) NOT NULL,
   task_description TEXT,
   task_due_date DATE
);

CREATE TABLE assessment_results (
   result_id SERIAL PRIMARY KEY,
   assessment_id INT REFERENCES regulatory_assessments(assessment_id),
   compliance_score INT,
   assessment_date DATE
);

CREATE TABLE compliance_details (
   detail_id SERIAL PRIMARY KEY,
   task_id INT REFERENCES compliance_tasks(task_id),
   result_id INT REFERENCES assessment_results(result_id),
   detail_description TEXT
);

CREATE TABLE regulatory_compliance_assessments (
    assessment_id INT PRIMARY KEY,
    assessment_name VARCHAR(50),
    assessment_status VARCHAR(20),
    assessment_date DATE
);

CREATE TABLE compliance_checklist (
    checklist_id INT PRIMARY KEY,
    checklist_name VARCHAR(50),
    checklist_description TEXT
);

CREATE TABLE compliance_issues (
    issue_id INT PRIMARY KEY,
    issue_description TEXT,
    issue_status VARCHAR(20)
);

CREATE TABLE compliance_audit (
    audit_id INT PRIMARY KEY,
    audit_date DATE,
    auditor_name VARCHAR(50),
    audit_status VARCHAR(20)
);

CREATE TABLE compliance_records (
    record_id INT PRIMARY KEY,
    record_type VARCHAR(50),
    record_date DATE,
    record_description TEXT
);
