tables.table_schema:
CREATE TABLE audits (
  audit_id INT PRIMARY KEY,
  audit_date DATE,
  auditor_name VARCHAR(50),
  audit_result VARCHAR(100)
);

CREATE TABLE inspections (
  inspection_id INT PRIMARY KEY,
  inspection_date DATE,
  inspector_name VARCHAR(50),
  inspection_result VARCHAR(100)
);

CREATE TABLE audit_docs (
  doc_id INT PRIMARY KEY,
  audit_id INT,
  doc_name VARCHAR(50),
  doc_type VARCHAR(50)
);

CREATE TABLE inspection_docs (
  doc_id INT PRIMARY KEY,
  inspection_id INT,
  doc_name VARCHAR(50),
  doc_type VARCHAR(50)
);

CREATE TABLE entities (
  entity_id INT PRIMARY KEY,
  entity_name VARCHAR(50),
  location VARCHAR(100)
);
