tables.table_schema:
CREATE TABLE data_retention_policy (
    id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    retention_period INT
);

CREATE TABLE retention_policies (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(50) NOT NULL,
    retention_period INT NOT NULL
);

CREATE TABLE regulatory_requirements (
    requirement_id INT PRIMARY KEY,
    requirement_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE policy_requirements (
    policy_id INT NOT NULL,
    requirement_id INT NOT NULL,
    FOREIGN KEY (policy_id) REFERENCES retention_policies(policy_id),
    FOREIGN KEY (requirement_id) REFERENCES regulatory_requirements(requirement_id)
);

CREATE TABLE retention_policies (
  policy_id INT PRIMARY KEY,
  policy_name TEXT,
  retention_period INTERVAL
);

CREATE TABLE tables_to_keep (
  table_name TEXT PRIMARY KEY,
  retention_policy_id INT REFERENCES retention_policies(policy_id)
);

CREATE TABLE data_to_retain (
  data_id SERIAL PRIMARY KEY,
  table_name TEXT REFERENCES tables_to_keep(table_name),
  retention_status BOOLEAN
);

CREATE TABLE audit_logs (
  log_id SERIAL PRIMARY KEY,
  data_id INT REFERENCES data_to_retain(data_id),
  action_taken TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE retention_policy_table (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(255),
    retention_period INT
);

CREATE TABLE policy_columns (
    column_id INT PRIMARY KEY,
    column_name VARCHAR(255),
    data_type VARCHAR(50),
    policy_id INT
);

CREATE TABLE encryption_keys (
    key_id INT PRIMARY KEY,
    key_name VARCHAR(255),
    encryption_algorithm VARCHAR(50),
    policy_id INT
);

CREATE TABLE access_control (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    role VARCHAR(50),
    policy_id INT
);
