tables.table_schema:
CREATE TABLE regulatory_reporting (
    id INT PRIMARY KEY,
    report_title TEXT,
    report_date DATE,
    reporter_name TEXT
);

CREATE TABLE regulatory_reporting (
    report_id INT PRIMARY KEY,
    report_name VARCHAR(255),
    report_date DATE,
    report_data BYTEA
);
CREATE TABLE regulatory_report_views (
    view_id INT PRIMARY KEY,
    view_name VARCHAR(255),
    view_description TEXT,
    report_id INT,
    FOREIGN KEY (report_id) REFERENCES regulatory_reporting(report_id)
);
