tables.table_schema:
CREATE TABLE EthicalDataValidationRules (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    age INT CHECK (age >= 18),
    email VARCHAR(255) UNIQUE
);

CREATE TABLE DataValidationLogs (
    log_id SERIAL PRIMARY KEY,
    entry_time TIMESTAMP NOT NULL,
    message TEXT
);

CREATE TABLE EthicalDataValidation (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    age INT CHECK (age >= 18),
    is_verified BOOLEAN DEFAULT FALSE
);

CREATE TABLE DataValidationLog (
    log_id SERIAL PRIMARY KEY,
    entry_date DATE DEFAULT CURRENT_DATE,
    description TEXT
);

CREATE TABLE EthicsRuleViolations (
    violation_id SERIAL PRIMARY KEY,
    user_id INT,
    violation_type VARCHAR(50),
    violation_date TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES EthicalDataValidation(id)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(100) NOT NULL,
    price DECIMAL(10, 2) NOT NULL CHECK (price >= 0),
    quantity INT NOT NULL CHECK (quantity >= 0)
);
