tables.table_schema:
CREATE TABLE environmental_standards (
    id INT PRIMARY KEY,
    standard_name VARCHAR(255),
    description TEXT,
    compliance_level INT
);

CREATE TABLE SustainabilityData (
    id INT PRIMARY KEY,
    business_id INT,
    category VARCHAR(50),
    value FLOAT
);

CREATE TABLE Business (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    location VARCHAR(100),
    industry VARCHAR(50)
);

CREATE TABLE Compliance (
    id INT PRIMARY KEY,
    business_id INT,
    standard VARCHAR(50),
    status VARCHAR(20)
);
