tables.table_schema:
CREATE TABLE board_meeting_minutes (
    meeting_id INT PRIMARY KEY,
    meeting_date DATE,
    agenda_topic TEXT,
    meeting_notes TEXT
);

CREATE TABLE board_meetings (
  meeting_id UUID PRIMARY KEY,
  meeting_date DATE NOT NULL,
  meeting_location VARCHAR(255) NOT NULL,
  meeting_duration INTERVAL NOT NULL
);

CREATE TABLE attendees (
  attendee_id UUID PRIMARY KEY,
  meeting_id UUID NOT NULL REFERENCES board_meetings(meeting_id),
  attendee_name VARCHAR(255) NOT NULL,
  attendee_email VARCHAR(255) NOT NULL
);

CREATE TABLE agenda_items (
  agenda_item_id UUID PRIMARY KEY,
  meeting_id UUID NOT NULL REFERENCES board_meetings(meeting_id),
  agenda_title VARCHAR(255) NOT NULL,
  agenda_description TEXT
);

CREATE TABLE minutes (
  minute_id UUID PRIMARY KEY,
  meeting_id UUID NOT NULL REFERENCES board_meetings(meeting_id),
  meeting_minutes TEXT NOT NULL,
  approved BOOLEAN DEFAULT false
);

CREATE TABLE BoardMembers (
    member_id INT PRIMARY KEY,
    full_name VARCHAR(255) NOT NULL,
    position VARCHAR(100),
    email VARCHAR(255) UNIQUE
);

CREATE TABLE MeetingAgendas (
    agenda_id INT PRIMARY KEY,
    meeting_date TIMESTAMP NOT NULL,
    agenda_details TEXT
);
