tables.table_schema:
CREATE TABLE conflicts_of_interest (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR,
    department VARCHAR,
    business_interest VARCHAR
);

CREATE TABLE relationship (
    relationship_id INT PRIMARY KEY,
    employee1_id INT,
    employee2_id INT,
    relationship_type VARCHAR
);

CREATE TABLE employees ( 
    employee_id INT PRIMARY KEY, 
    full_name TEXT, 
    position TEXT, 
    department TEXT
);

CREATE TABLE business_interests ( 
    interest_id INT PRIMARY KEY, 
    interest_name TEXT, 
    description TEXT, 
    category TEXT
);

CREATE TABLE business_interests (
	business_interest_id INT PRIMARY KEY,
	business_name VARCHAR(50),
	industry VARCHAR(50),
	location VARCHAR(50)
);

CREATE TABLE conflict_reports (
	report_id INT PRIMARY KEY,
	conflict_id INT,
	report_description TEXT,
	report_date DATE
);
