tables.table_schema:
CREATE TABLE RiskAssessmentData (
    company_id INT,
    date DATE,
    governance_practices VARCHAR,
    outcome VARCHAR
);

CREATE TABLE GovernanceScores (
    company_id INT,
    date DATE,
    score INT
);

CREATE TABLE risk_assessment (
    company_id INT PRIMARY KEY,
    governance_practice TEXT,
    risk_score FLOAT
);

CREATE TABLE RiskAssessment (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    assess_date DATE,
    risk_score DECIMAL
);

CREATE TABLE GovernancePractices (
    id INT PRIMARY KEY,
    company_id INT,
    practice_description TEXT,
    practice_date DATE
);

CREATE TABLE OutcomeAnalysis (
    id INT PRIMARY KEY,
    company_id INT,
    outcome_type VARCHAR(50),
    outcome_date DATE
);

CREATE TABLE HistoricalData (
    id INT PRIMARY KEY,
    company_id INT,
    data_type VARCHAR(50),
    data_value DECIMAL
);

CREATE TABLE CompanyInfo (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    industry_sector VARCHAR(50),
    headquarters_location VARCHAR(100)
);
