tables.table_schema:
CREATE TABLE action_plan (
    action_plan_id INT PRIMARY KEY,
    issue_id INT,
    description TEXT,
    due_date DATE
);

CREATE TABLE audit_issue (
    issue_id INT PRIMARY KEY,
    audit_id INT,
    description TEXT,
    priority INT
);

CREATE TABLE internal_audit (
    audit_id INT PRIMARY KEY,
    audit_date DATE,
    auditor_name VARCHAR(255),
    department VARCHAR(255)
);

CREATE TABLE governance_issues (
    issue_id INT PRIMARY KEY,
    issue_description STRING,
    status STRING
);

CREATE TABLE internal_audits (
    audit_id INT PRIMARY KEY,
    audit_date DATE,
    auditor_name STRING,
    audit_description STRING
);

CREATE TABLE ActionPlans (
	action_plan_id INT PRIMARY KEY,
	issue_id INT,
	department VARCHAR,
	due_date DATE
);

CREATE TABLE Issues (
	issue_id INT PRIMARY KEY,
	issue_description TEXT,
	status VARCHAR
);

CREATE TABLE Audits (
	audit_id INT PRIMARY KEY,
	auditor VARCHAR,
	audit_date DATE
);

CREATE TABLE Departments (
	department_id INT PRIMARY KEY,
	department_name VARCHAR
);

CREATE TABLE Assignments (
	assignment_id INT PRIMARY KEY,
	audit_id INT,
	action_plan_id INT
);
