tables.table_schema:
CREATE TABLE MarketData (
  date DATE,
  symbol VARCHAR(10),
  price DECIMAL,
  volume INT
);

CREATE TABLE InvestmentAccounts (
  account_id INT,
  customer_id INT,
  account_type VARCHAR(20),
  balance DECIMAL
);

CREATE TABLE RiskAssessment (
  date DATE,
  account_id INT,
  risk_score INT,
  recommendation VARCHAR(50)
);

CREATE TABLE market_data (
    id INT PRIMARY KEY,
    stock_symbol VARCHAR(20),
    stock_price DECIMAL(10, 2),
    market_cap DECIMAL(20, 2)
);

CREATE TABLE company_info (
    id INT PRIMARY KEY,
    company_name VARCHAR(50),
    industry VARCHAR(50),
    founding_year INT
);

CREATE TABLE investor_portfolio (
    id INT PRIMARY KEY,
    investor_name VARCHAR(50),
    investment_amount DECIMAL(15, 2),
    stock_symbol VARCHAR(20)
);

CREATE TABLE market_trends (
    id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT,
    trend_type VARCHAR(20)
);

CREATE TABLE ExchangeRates (
    currency_code VARCHAR(3) PRIMARY KEY,
    exchange_rate DECIMAL(10, 4) NOT NULL,
    last_updated TIMESTAMP NOT NULL
);

CREATE TABLE RiskAssessment (
    risk_id INT PRIMARY KEY,
    investment_id INT NOT NULL,
    risk_score DECIMAL(5, 2) NOT NULL,
    assessment_date TIMESTAMP NOT NULL
);

CREATE TABLE Analysts (
    analyst_id INT PRIMARY KEY,
    analyst_name VARCHAR(255) NOT NULL,
    expertise_area VARCHAR(100) NOT NULL
);
