tables.table_schema:
CREATE TABLE EmployeeTrainingRecords (
    employee_id INT PRIMARY KEY,
    training_date DATE,
    training_topic VARCHAR(100),
    completion_status BOOLEAN
);

CREATE TABLE safety_regulations (
    regulation_id SERIAL PRIMARY KEY,
    regulation_name VARCHAR(100) NOT NULL,
    regulation_description TEXT
);

CREATE TABLE Employee ( 
  employee_id INT PRIMARY KEY, 
  employee_name VARCHAR(50) NOT NULL, 
  department VARCHAR(50) NOT NULL 
);

CREATE TABLE TrainingProgram ( 
  program_id INT PRIMARY KEY, 
  program_name VARCHAR(50) NOT NULL, 
  program_type VARCHAR(50) NOT NULL 
);

CREATE TABLE Department ( 
  department_id INT PRIMARY KEY, 
  department_name VARCHAR(50) NOT NULL 
);

CREATE TABLE SafetyRegulation ( 
  regulation_id INT PRIMARY KEY, 
  regulation_name VARCHAR(50) NOT NULL, 
  department_id INT, 
  description TEXT 
);
