tables.table_schema:
CREATE TABLE ThreatData (
    threat_id INT PRIMARY KEY,
    threat_type VARCHAR(50) NOT NULL,
    threat_description TEXT,
    threat_severity INT
);

CREATE TABLE MitigationStrategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50) NOT NULL,
    strategy_description TEXT
);

CREATE TABLE threats (
    threat_id INT PRIMARY KEY,
    threat_name VARCHAR(50) NOT NULL,
    threat_type VARCHAR(50) NOT NULL,
    threat_level INT
);

CREATE TABLE targets (
    target_id INT PRIMARY KEY,
    target_name VARCHAR(50) NOT NULL,
    target_ip_address VARCHAR(15) NOT NULL,
    target_location VARCHAR(100)
);

CREATE TABLE mitigation_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(100) NOT NULL,
    strategy_description TEXT,
    target_id INT
);
