tables.table_schema:
CREATE TABLE employee_compliance_training (
    employee_id INT,
    employee_name VARCHAR(50),
    training_module VARCHAR(100),
    completion_date DATE
);

CREATE TABLE compliance_training_records (
   employee_id INT PRIMARY KEY,
   training_date DATE,
   training_completed BOOLEAN
);

CREATE TABLE employee_info (
   employee_id INT PRIMARY KEY,
   employee_name VARCHAR(50),
   department VARCHAR(50)
);

CREATE TABLE employees (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50) NOT NULL,
    department VARCHAR(50) NOT NULL
);

CREATE TABLE training_records (
    record_id INT PRIMARY KEY,
    employee_id INT NOT NULL,
    training_date DATE NOT NULL,
    training_description TEXT
);

CREATE TABLE compliance_requirements (
    requirement_id INT PRIMARY KEY,
    requirement_name VARCHAR(50) NOT NULL,
    requirement_description TEXT
);

CREATE TABLE Employees (
    employee_id INT PRIMARY KEY,
    first_name TEXT,
    last_name TEXT,
    department TEXT
);

CREATE TABLE TrainingPrograms (
    program_id INT PRIMARY KEY,
    program_name TEXT,
    required_department TEXT,
    completion_due_date DATE
);

CREATE TABLE EmployeeTrainingRecords (
    record_id INT PRIMARY KEY,
    employee_id INT REFERENCES Employees(employee_id),
    program_id INT REFERENCES TrainingPrograms(program_id),
    completion_date DATE
);

CREATE TABLE ComplianceDocuments (
    document_id INT PRIMARY KEY,
    document_name TEXT,
    document_description TEXT,
    document_path TEXT
);
