tables.table_schema:
CREATE TABLE government_agencies (
    agency_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE public_records (
    record_id INT PRIMARY KEY,
    record_data TEXT NOT NULL,
    agency_id INT NOT NULL,
    FOREIGN KEY (agency_id) REFERENCES government_agencies(agency_id)
);

CREATE TABLE transparency_laws (
    law_id INT PRIMARY KEY,
    law_text TEXT NOT NULL,
    agency_id INT NOT NULL,
    FOREIGN KEY (agency_id) REFERENCES government_agencies(agency_id)
);

CREATE TABLE agencies (
   id INT PRIMARY KEY,
   name VARCHAR(255),
   city VARCHAR(255),
   state VARCHAR(2)
);

CREATE TABLE transparency_laws (
   id INT PRIMARY KEY,
   law_name VARCHAR(255),
   description TEXT,
   compliance_deadline DATE
);
