CREATE TABLE document_contents (
    id SERIAL PRIMARY KEY,
    document_id INT NOT NULL,
    content TEXT NOT NULL,
    FOREIGN KEY (document_id) REFERENCES compliance_documents(id)
);

CREATE TABLE audits (
    id SERIAL PRIMARY KEY,
    audit_date DATE NOT NULL,
    body_id INT NOT NULL,
    FOREIGN KEY (body_id) REFERENCES regulatory_bodies(id)
);

CREATE TABLE regulatory_bodies (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE auditors (
    auditor_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    certification_status BOOLEAN NOT NULL
);
