tables.table_schema:
CREATE TABLE PatientConsentForms (
    patient_id INT PRIMARY KEY,
    consent_date DATE,
    consent_given BOOLEAN
);

CREATE TABLE HealthcareProviders (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(50),
    specialty VARCHAR(50)
);

CREATE TABLE ComplianceRegulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(50),
    compliance_status BOOLEAN
);

CREATE TABLE patient_forms (
    patient_id INT PRIMARY KEY,
    form_id INT,
    consent_date TIMESTAMP,
    consent_text TEXT
);

CREATE TABLE physicians (
    physician_id INT PRIMARY KEY,
    name VARCHAR(50),
    specialty VARCHAR(50),
    license_number VARCHAR(20)
);

CREATE TABLE organizations (
    org_id INT PRIMARY KEY,
    org_name VARCHAR(100),
    address VARCHAR(255),
    contact_person VARCHAR(50)
);

CREATE TABLE healthcare_providers (
  provider_id SERIAL PRIMARY KEY,
  provider_name TEXT,
  speciality TEXT
);

CREATE TABLE consent_audit_logs (
  log_id SERIAL PRIMARY KEY,
  consent_id INT,
  action TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE provider_patient_associations (
  association_id SERIAL PRIMARY KEY,
  provider_id INT,
  patient_id INT
);
