tables.table_schema:
CREATE TABLE environmental_impact_assessments (
    id INT PRIMARY KEY,
    project_name TEXT NOT NULL,
    location TEXT NOT NULL,
    assessment_date DATE
);

CREATE TABLE permit_requirements (
    permit_id INT PRIMARY KEY,
    permit_description TEXT NOT NULL,
    project_id INT,
    FOREIGN KEY (project_id) REFERENCES environmental_impact_assessments(id)
);

CREATE TABLE environmental_impact_assessments (
	eia_id INT PRIMARY KEY,
	eia_date DATE,
	eia_location VARCHAR(255),
	eia_description TEXT
);

CREATE TABLE permit_requirements (
	permit_id INT PRIMARY KEY,
	permit_type VARCHAR(100),
	permit_description TEXT,
	validity_period DATE
);

CREATE TABLE compliance_records (
	record_id INT PRIMARY KEY,
	issue_date DATE,
	compliance_status VARCHAR(50),
	comments TEXT
);

CREATE TABLE environmental_impact_assessment (
	author_name VARCHAR,
	date DATE,
	location VARCHAR,
	assessment_results TEXT
);

CREATE TABLE regulations (
	regulation_id INT,
	regulation_title VARCHAR,
	description TEXT,
	category VARCHAR
);

CREATE TABLE company_details (
	company_name VARCHAR,
	address VARCHAR,
	contact_person VARCHAR,
	email VARCHAR
);

CREATE TABLE impact_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255)
);

CREATE TABLE assessment_categories (
    assessment_id INT,
    category_id INT,
    FOREIGN KEY (assessment_id) REFERENCES environmental_impact_assessment(id),
    FOREIGN KEY (category_id) REFERENCES impact_categories(category_id)
);

CREATE TABLE permit_requirements (
    requirement_id INT PRIMARY KEY,
    requirement_name VARCHAR(255)
);
