tables.table_schema:
CREATE TABLE ValidationReconciliation (
    validation_id SERIAL PRIMARY KEY,
    process_name VARCHAR(50) NOT NULL,
    validation_type VARCHAR(50) NOT NULL,
    validation_date DATE
);

CREATE TABLE validation_processes (
    process_id INT PRIMARY KEY,
    process_name VARCHAR(255) NOT NULL,
    process_description TEXT
);

CREATE TABLE reconciliation_results (
    result_id INT PRIMARY KEY,
    process_id INT NOT NULL,
    result VARCHAR(255) NOT NULL,
    result_date TIMESTAMP
);

CREATE TABLE validation_processes (
    process_id UUID PRIMARY KEY,
    process_name VARCHAR(255) NOT NULL,
    process_description TEXT
);

CREATE TABLE data_validation (
    validation_id UUID PRIMARY KEY,
    process_id UUID REFERENCES validation_processes(process_id),
    validation_type VARCHAR(50) NOT NULL,
    validation_status VARCHAR(20) DEFAULT 'pending',
    validation_result BOOLEAN
);

CREATE TABLE reconciliation_logs (
    log_id UUID PRIMARY KEY,
    validation_id UUID REFERENCES data_validation(validation_id),
    log_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    log_details TEXT
);

CREATE TABLE validation_rules (
    rule_id INT PRIMARY KEY,
    rule_name VARCHAR(255) NOT NULL,
    description TEXT,
    created_at TIMESTAMP
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    connection_string VARCHAR(255),
    created_at TIMESTAMP
);

CREATE TABLE validation_results (
    result_id UUID PRIMARY KEY,
    job_id UUID NOT NULL,
    result_text TEXT,
    result_status ENUM('pass', 'fail') NOT NULL
);

CREATE TABLE validation_logs (
    log_id UUID PRIMARY KEY,
    job_id UUID NOT NULL,
    log_text TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE data_sources (
    source_id UUID PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    connection_string TEXT NOT NULL
);

CREATE TABLE validation_errors (
    error_id UUID PRIMARY KEY,
    result_id UUID NOT NULL,
    error_text TEXT NOT NULL,
    error_timestamp TIMESTAMP NOT NULL
);
