tables.table_schema:
CREATE TABLE BackupSettings (
    backup_id UUID PRIMARY KEY,
    backup_type VARCHAR(50),
    storage_location VARCHAR(100)
);

CREATE TABLE RecoveryStrategies (
    strategy_id UUID PRIMARY KEY,
    strategy_name VARCHAR(50),
    strategy_description TEXT
);

CREATE TABLE backups (
    backup_id INT PRIMARY KEY,
    backup_name VARCHAR(255),
    backup_location VARCHAR(255),
    backup_date DATE
);

CREATE TABLE recovery_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    strategy_description TEXT,
    strategy_owner VARCHAR(255)
);

CREATE TABLE data_protection (
    protection_id INT PRIMARY KEY,
    data_type VARCHAR(255),
    protection_level VARCHAR(255),
    backup_frequency VARCHAR(255)
);

CREATE TABLE backup_policies (
    policy_id INT PRIMARY KEY,
    policy_name STRING,
    backup_frequency STRING,
    retention_period INT
);

CREATE TABLE backup_logs (
    log_id INT PRIMARY KEY,
    policy_id INT REFERENCES backup_policies (policy_id),
    backup_status STRING,
    backup_timestamp TIMESTAMP
);

CREATE TABLE restore_points (
    point_id INT PRIMARY KEY,
    log_id INT REFERENCES backup_logs (log_id),
    restore_timestamp TIMESTAMP
);

CREATE TABLE disaster_recovery_plans (
    plan_id INT PRIMARY KEY,
    plan_name STRING,
    recovery_strategy STRING,
    contact_details STRING
);
