tables.table_schema:
CREATE TABLE performance_tuning (
  query_id INT PRIMARY KEY,
  query_text TEXT,
  optimization_technique TEXT
);

CREATE TABLE PerformanceMetrics (
    id UUID PRIMARY KEY,
    query_id UUID,
    execution_time INT,
    indexes_used BOOLEAN
);

CREATE TABLE QueryLogs (
    log_id UUID PRIMARY KEY,
    query TEXT,
    execution_timestamp TIMESTAMP,
    execution_duration INT
);

CREATE TABLE Indexes (
    index_id UUID PRIMARY KEY,
    table_name TEXT,
    index_name TEXT,
    indexed_columns TEXT[]
);

CREATE TABLE Query_Execution_Stats (
    query_id INT PRIMARY KEY,
    query_execution_time FLOAT,
    query_execution_date DATE
);
