tables.table_schema:
CREATE TABLE money_laundering_transactions (
    transaction_id INT PRIMARY KEY,
    customer_id INT,
    transaction_amount DECIMAL,
    transaction_date DATE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    name VARCHAR,
    address VARCHAR
);

CREATE TABLE suspicious_users (
    user_id INT PRIMARY KEY,
    name VARCHAR,
    address VARCHAR,
    report_type VARCHAR
);

CREATE TABLE reports (
    report_id INT PRIMARY KEY,
    report_type VARCHAR,
    report_date DATE,
    description TEXT
);
