CREATE TABLE permits (
 permit_id INT PRIMARY KEY,
 facility_name VARCHAR(50),
 activity_type VARCHAR(50),
 issue_date DATE
);

CREATE TABLE facilities (
 facility_id INT PRIMARY KEY,
 facility_name VARCHAR(50),
 location VARCHAR(100),
