tables.table_schema:
CREATE TABLE pollutant_data (
  pollutant_id UUID PRIMARY KEY,
  pollutant_name STRING,
  pollutant_level FLOAT,
  measurement_date DATE
);

CREATE TABLE pollutant_levels (
   id INT PRIMARY KEY,
   pollutant_name TEXT,
   level DECIMAL,
   date_measured TIMESTAMP
);

CREATE TABLE measurement_locations (
   location_id INT PRIMARY KEY,
   location_name TEXT,
   latitude DECIMAL,
   longitude DECIMAL
);

CREATE TABLE pollutant_sources (
   source_id INT PRIMARY KEY,
   source_name TEXT,
   source_type TEXT,
   source_description TEXT
);

CREATE TABLE pollutant_levels (
  id INT PRIMARY KEY,
  pollutant_name VARCHAR(255),
  level FLOAT,
  date_time TIMESTAMP
);

CREATE TABLE locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255),
  latitude DECIMAL,
  longitude DECIMAL
);

CREATE TABLE sensors (
  sensor_id INT PRIMARY KEY,
  sensor_name VARCHAR(255),
  location_id INT,
  FOREIGN KEY (location_id) REFERENCES locations(location_id)
);
