tables.table_schema:
CREATE TABLE RegulatoryAgencies (
  agency_id INT PRIMARY KEY,
  agency_name VARCHAR(255),
  regulations_text TEXT,
  contact_person VARCHAR(255)
);

CREATE TABLE regulations (
    id UUID PRIMARY KEY,
    regulation_name STRING,
    regulation_date DATE,
    regulatory_body STRING
);

CREATE TABLE HazardousMaterials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(255) NOT NULL,
  material_type VARCHAR(50) NOT NULL
);

CREATE TABLE DisposalSites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255) NOT NULL,
  site_location VARCHAR(100) NOT NULL
);

CREATE TABLE DisposalRecords (
  record_id INT PRIMARY KEY,
  material_id INT NOT NULL,
  site_id INT NOT NULL,
  disposal_date DATE NOT NULL
);

CREATE TABLE Regulations (
  regulation_id INT PRIMARY KEY,
  regulation_description TEXT
);

CREATE TABLE MaterialRegulations (
  material_id INT NOT NULL,
  regulation_id INT NOT NULL,
  PRIMARY KEY (material_id, regulation_id)
);
