tables.table_schema:
CREATE TABLE noise_levels (
    id SERIAL PRIMARY KEY,
    location VARCHAR(255),
    decibel_level INT,
    date_recorded DATE
);

CREATE TABLE complaints (
    id SERIAL PRIMARY KEY,
    location VARCHAR(255),
    complaint_text TEXT,
    date_submitted DATE
);

CREATE TABLE NoisePollutionRegulations (
  id INT PRIMARY KEY,
  residential_area_id INT,
  noise_level DECIMAL
);

CREATE TABLE ResidentialAreas (
  id INT PRIMARY KEY,
  area_name VARCHAR,
  postal_code VARCHAR
);

CREATE TABLE NoiseComplaints (
  id INT PRIMARY KEY,
  residential_area_id INT,
  complaint_date TIMESTAMP,
  complaint_details TEXT
);

CREATE TABLE noise_levels (
  location_id INT PRIMARY KEY,
  noise_level DECIMAL
);

CREATE TABLE noise_regulations (
  regulation_id INT PRIMARY KEY,
  max_allowable_level DECIMAL
);

CREATE TABLE residential_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50),
  area_size DECIMAL
);

CREATE TABLE noise_violations (
  violation_id INT PRIMARY KEY,
  location_id INT,
  violation_date DATE
);
