tables.table_schema:
CREATE TABLE WaterUsageData (
  id INT PRIMARY KEY,
  customer_id INT,
  usage_date DATE,
  gallons_used FLOAT
);

CREATE TABLE CustomerInfo (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  address VARCHAR(100),
  email VARCHAR(50)
);

CREATE TABLE WaterUsageData ( 
    id SERIAL PRIMARY KEY, 
    user_id INT NOT NULL, 
    water_consumed DOUBLE PRECISION NOT NULL, 
    date_recorded TIMESTAMP NOT NULL 
);

CREATE TABLE UserInformation ( 
    user_id SERIAL PRIMARY KEY, 
    username VARCHAR(50) NOT NULL, 
    email VARCHAR(100) NOT NULL 
);

CREATE TABLE ComplianceStatus ( 
    user_id INT NOT NULL, 
    date_checked DATE NOT NULL, 
    is_compliant BOOLEAN NOT NULL 
);

CREATE TABLE water_sources (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL
);

CREATE TABLE water_regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name VARCHAR(255) NOT NULL
);

CREATE TABLE WaterConsumptionData (
    id INT PRIMARY KEY,
    date DATE NOT NULL,
    location VARCHAR(50) NOT NULL,
    usage_gallons INT NOT NULL
);

CREATE TABLE WaterMeterReading (
    meter_id INT PRIMARY KEY,
    reading DATE NOT NULL,
    value_gallons INT NOT NULL
);

CREATE TABLE ComplianceReports (
    report_id INT PRIMARY KEY,
    submitted_date DATE NOT NULL,
    compliance_status VARCHAR(20) NOT NULL
);

CREATE TABLE WaterUsageThresholds (
    threshold_id INT PRIMARY KEY,
    location VARCHAR(50) NOT NULL,
    threshold_gallons INT NOT NULL
);

CREATE TABLE WaterProviders (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(50) NOT NULL
);
