tables.table_schema:
CREATE TABLE protected_areas (
    id BIGINT PRIMARY KEY,
    area_name STRING,
    location GEOGRAPHY,
    area_type STRING
);
CREATE TABLE conservation_categories (
    id BIGINT PRIMARY KEY,
    category_name STRING
);

CREATE TABLE ProtectedAreas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR,
    area_type VARCHAR,
    area_description TEXT
);

CREATE TABLE Coordinates (
    coord_id INT PRIMARY KEY,
    area_id INT REFERENCES ProtectedAreas(area_id),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE ParkRangers (
    ranger_id INT PRIMARY KEY,
    name VARCHAR,
    area_id INT REFERENCES ProtectedAreas(area_id),
    hired_date DATE
);

CREATE TABLE protected_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(100),
    area_type VARCHAR(50),
    coordinates GEOMETRY
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(100),
    researcher_email VARCHAR(100)
);

CREATE TABLE protected_areas (
  area_id INT PRIMARY KEY,
