);

CREATE TABLE wildlife_species (
  species_id INT PRIMARY KEY,
  species_name TEXT,
  species_description TEXT
);

CREATE TABLE habitat_types (
  habitat_id INT PRIMARY KEY,
  habitat_name TEXT,
  habitat_description TEXT
);

CREATE TABLE species_habitats (
  species_id INT,
  habitat_id INT
);

CREATE TABLE area_species (
  area_id INT,
  species_id INT
);
