tables.table_schema:
CREATE TABLE user_data_deletion_requests (
    request_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    request_date DATE NOT NULL,
    deletion_status VARCHAR(50) NOT NULL
);

CREATE TABLE user_data_deletion_requests (
    request_id INT PRIMARY KEY,
    user_id INT,
    deletion_reason TEXT,
    request_date DATE
);

CREATE TABLE deletion_request_status (
    request_id INT PRIMARY KEY,
    status TEXT,
    processing_date DATE
);

CREATE TABLE UserRequests (
    request_id INT PRIMARY KEY,
    user_id INT,
    request_date DATE,
    status VARCHAR(20)
);

CREATE TABLE RequestLogs (
    log_id INT PRIMARY KEY,
    request_id INT,
    log_date DATE,
    description TEXT
);

CREATE TABLE DataDeletion (
    deletion_id INT PRIMARY KEY,
    user_id INT,
    data_type VARCHAR(50),
    deletion_date DATE
);

CREATE TABLE deletion_requests (
    request_id INT PRIMARY KEY,
    user_id INT,
    requested_at TIMESTAMP,
    status VARCHAR(50)
);

CREATE TABLE deletion_logs (
    log_id INT PRIMARY KEY,
    request_id INT,
    log_timestamp TIMESTAMP,
    action_taken VARCHAR(100)
);

CREATE TABLE data_processing_status (
    request_id INT PRIMARY KEY,
    processing_status VARCHAR(50),
    completion_timestamp TIMESTAMP
);

CREATE TABLE user_verification (
    verification_id INT PRIMARY KEY,
    user_id INT,
    verification_code VARCHAR(100),
    verification_status VARCHAR(50)
);
