tables.table_schema:
CREATE TABLE Employee_Work_Hours (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    work_hours INT,
    overtime_eligibility BOOLEAN
);

CREATE TABLE Employee (
    employee_id INT PRIMARY KEY,
    full_name VARCHAR(50) NOT NULL,
    hourly_rate DECIMAL(10, 2) NOT NULL
);

CREATE TABLE WorkHours (
    entry_id INT PRIMARY KEY,
    employee_id INT NOT NULL,
    work_date DATE NOT NULL,
    hours_worked DECIMAL(5, 2) NOT NULL
);

CREATE TABLE OvertimeEligibility (
    employee_id INT PRIMARY KEY,
    eligible_for_overtime BOOLEAN NOT NULL
);

CREATE TABLE Employees (
    employee_id INT PRIMARY KEY,
    full_name VARCHAR(255),
    position VARCHAR(100),
    hourly_rate DECIMAL(10, 2)
);

CREATE TABLE TimeCards (
    timecard_id INT PRIMARY KEY,
    employee_id INT,
    time_in TIMESTAMP,
    time_out TIMESTAMP
);

CREATE TABLE OvertimeRules (
    rule_id INT PRIMARY KEY,
    min_hours DECIMAL(5, 2),
    rate_multiplier DECIMAL(3, 2)
);

CREATE TABLE OvertimeLogs (
    log_id INT PRIMARY KEY,
    employee_id INT,
    overtime_hours DECIMAL(5, 2),
    overtime_date DATE
);

CREATE TABLE employees (
    employee_id UUID PRIMARY KEY,
    employee_name VARCHAR(255) NOT NULL,
    hire_date DATE NOT NULL,
    department VARCHAR(50) NOT NULL
);

CREATE TABLE overtime_eligibility (
    eligibility_id UUID PRIMARY KEY,
    employee_id UUID NOT NULL,
    overtime_eligible BOOLEAN NOT NULL
);

CREATE TABLE labor_laws (
    law_id UUID PRIMARY KEY,
    law_name VARCHAR(255) NOT NULL,
    law_description TEXT NOT NULL
);

CREATE TABLE violation_reports (
    report_id UUID PRIMARY KEY,
    employee_id UUID NOT NULL,
    violation_date DATE NOT NULL,
    violation_description TEXT NOT NULL
);
