tables.table_schema:
CREATE TABLE EmployeeGrievances (
  grievance_id INT PRIMARY KEY,
  employee_id INT,
  grievance_details TEXT,
  date_filed DATE
);

CREATE TABLE GrievanceResolutions (
  resolution_id INT PRIMARY KEY,
  grievance_id INT,
  resolution_details TEXT,
  resolution_date DATE
);

CREATE TABLE resolutions (
    id INT PRIMARY KEY,
    grievance_id INT,
    resolution_description TEXT,
    date_resolved DATE
);

CREATE TABLE employees (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    department VARCHAR(50),
    position VARCHAR(50)
);

CREATE TABLE Departments (
    department_id INT,
    department_name VARCHAR(50),
    department_head VARCHAR(50),
    PRIMARY KEY (department_id)
);

CREATE TABLE Employee (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    department VARCHAR(50),
    position VARCHAR(50)
);

CREATE TABLE Grievance (
    id INT PRIMARY KEY,
    description TEXT,
    date_filed DATE,
    employee_id INT
);

CREATE TABLE Resolution (
    id INT PRIMARY KEY,
    description TEXT,
    date_resolved DATE,
    grievance_id INT
);

CREATE TABLE Department (
    id INT PRIMARY KEY,
    name VARCHAR(50)
);

CREATE TABLE Position (
    id INT PRIMARY KEY,
    title VARCHAR(50)
);
