CREATE TABLE diversity_metrics (
    metric_id INT PRIMARY KEY,
    employee_id INT,
    diversity_category VARCHAR(50),
    metric_value DECIMAL
);

CREATE TABLE diversity_metrics (department VARCHAR(255) PRIMARY KEY,
    diversity_index DECIMAL,
    representation_pct DECIMAL);

CREATE TABLE employee_equality_info (
    employee_id INT PRIMARY KEY,
    sexual_orientation VARCHAR(50),
    gender_identity VARCHAR(50),
    diversity_hiring_initiative BOOLEAN
);
