tables.table_schema:
CREATE TABLE employment_laws (
  law_id UUID PRIMARY KEY,
  law_name VARCHAR(100),
  law_description TEXT
);

CREATE TABLE employment_laws (
    law_id INT PRIMARY KEY,
    law_title VARCHAR(100),
    law_description TEXT
);

CREATE TABLE Employees (
employee_id SERIAL PRIMARY KEY,
employee_name VARCHAR(255) NOT NULL,
employee_department VARCHAR(255),
employee_position VARCHAR(255)
);

CREATE TABLE Goals (
goal_id SERIAL PRIMARY KEY,
employee_id INT,
goal_description TEXT,
goal_target_date DATE
);

CREATE TABLE employees (
   employee_id INT PRIMARY KEY,
   employee_name VARCHAR(255),
   department VARCHAR(50),
   hire_date DATE
);

CREATE TABLE appraisal_cycles (
   cycle_id INT PRIMARY KEY,
   start_date DATE,
   end_date DATE,
   status VARCHAR(20)
);

CREATE TABLE appraisal_ratings (
   rating_id INT PRIMARY KEY,
   rating_name VARCHAR(50),
   description TEXT
);

CREATE TABLE employee_feedback (
   feedback_id INT PRIMARY KEY,
   review_id INT,
   employee_id INT,
   feedback_text TEXT
);
