tables.table_schema:
CREATE TABLE job_descriptions (
    job_id INT PRIMARY KEY,
    job_title VARCHAR(100),
    job_description TEXT
);

CREATE TABLE qualifications (
    qualification_id INT PRIMARY KEY,
    job_id INT REFERENCES job_descriptions(job_id),
    qualification_description TEXT
);

CREATE TABLE qualifications (
    qualification_id INT PRIMARY KEY,
    qualification_description TEXT
);

CREATE TABLE job_qualifications (
    job_id INT,
    qualification_id INT,
    FOREIGN KEY (job_id) REFERENCES job_descriptions(job_id),
    FOREIGN KEY (qualification_id) REFERENCES qualifications(qualification_id)
);

CREATE TABLE JobDescriptions (
    job_id INT PRIMARY KEY,
    job_title VARCHAR(255) NOT NULL,
    job_description TEXT,
    minimum_qualification VARCHAR(255)
);

CREATE TABLE Qualifications (
    qualification_id INT PRIMARY KEY,
    qualification_name VARCHAR(255) NOT NULL,
    qualification_description TEXT
);

CREATE TABLE Applicants (
    applicant_id INT PRIMARY KEY,
    applicant_name VARCHAR(255) NOT NULL,
    applicant_email VARCHAR(255),
    applicant_phone VARCHAR(15)
);

CREATE TABLE ApplicationQualifications (
    application_id INT,
    qualification_id INT
);
