tables.table_schema:
CREATE TABLE emergency_shelters (
  shelter_id SERIAL PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  capacity INT NOT NULL,
  available_resources TEXT
);

CREATE TABLE shelters ( 
  shelter_id INT PRIMARY KEY, 
  location VARCHAR(255), 
  capacity INT, 
  available_resources VARCHAR(255) 
);

CREATE TABLE shelter_contacts ( 
  contact_id INT PRIMARY KEY, 
  shelter_id INT, 
  contact_name VARCHAR(100), 
  contact_number VARCHAR(20) 
);

CREATE TABLE emergency_locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255) NOT NULL,
  address VARCHAR(255) NOT NULL,
  capacity INT NOT NULL
);

CREATE TABLE shelter_capacity (
  shelter_id INT PRIMARY KEY,
  location_id INT NOT NULL,
  capacity INT NOT NULL,
  available_spaces INT NOT NULL
);

CREATE TABLE shelter_resources (
  resource_id INT PRIMARY KEY,
  shelter_id INT NOT NULL,
  resource_type VARCHAR(50) NOT NULL,
  quantity INT NOT NULL
);

CREATE TABLE IF NOT EXISTS Capacities (
  capacity_id UUID PRIMARY KEY,
  location_id UUID REFERENCES Locations(location_id),
  max_capacity INT NOT NULL,
  current_occupancy INT NOT NULL
);

CREATE TABLE IF NOT EXISTS Resources (
  resource_id UUID PRIMARY KEY,
  resource_name TEXT NOT NULL,
  quantity INT NOT NULL
);

CREATE TABLE IF NOT EXISTS ShelterResources (
  shelter_resource_id UUID PRIMARY KEY,
  location_id UUID REFERENCES Locations(location_id),
  resource_id UUID REFERENCES Resources(resource_id),
  quantity_available INT NOT NULL
);

CREATE TABLE emergency_shelters (
  shelter_id INT PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  capacity INT NOT NULL,
  available_resources VARCHAR(500),
);

CREATE TABLE shelter_contacts (
  contact_id INT PRIMARY KEY,
  shelter_id INT NOT NULL,
  contact_name VARCHAR(255) NOT NULL,
  contact_number VARCHAR(20) NOT NULL,
);

CREATE TABLE resource_inventory (
  resource_id INT PRIMARY KEY,
  shelter_id INT NOT NULL,
  resource_name VARCHAR(255) NOT NULL,
  quantity INT NOT NULL,
);

CREATE TABLE shelter_occupancy (
  occupancy_id INT PRIMARY KEY,
  shelter_id INT NOT NULL,
  occupancy_date DATE NOT NULL,
  total_occupants INT NOT NULL,
);

CREATE TABLE shelter_status (
  status_id INT PRIMARY KEY,
  shelter_id INT NOT NULL,
  status_code INT NOT NULL,
  status_description TEXT,
);
