tables.table_schema:
CREATE TABLE impact_indicators (
    indicator_id SERIAL PRIMARY KEY,
    indicator_name VARCHAR(100) NOT NULL,
    description TEXT,
    weightage DECIMAL(4, 2) NOT NULL
);

CREATE TABLE project_impact (
    project_id INT REFERENCES projects(project_id),
    indicator_id INT REFERENCES impact_indicators(indicator_id),
    impact_score DECIMAL(5, 2) NOT NULL,
    PRIMARY KEY (project_id, indicator_id)
);

CREATE TABLE EvaluationMetrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(255),
    metric_description TEXT,
    metric_type VARCHAR(50)
);
