tables.table_schema:
CREATE TABLE AgriculturalOutputs (
    id INT PRIMARY KEY,
    crop_name TEXT,
    production_quantity INT,
    production_date DATE
);

CREATE TABLE MarketPrices (
    id INT PRIMARY KEY,
    crop_name TEXT,
    price_per_quantity FLOAT,
    market_location TEXT
);

CREATE TABLE crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    crop_type VARCHAR(50) NOT NULL,
    crop_growth_period VARCHAR(100) NOT NULL
);

CREATE TABLE agriculture_outputs (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    production_quantity DECIMAL,
    production_date DATE
);

CREATE TABLE market_prices (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    price DECIMAL,
    price_date DATE
);

CREATE TABLE regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(255),
    country VARCHAR(255)
);

CREATE TABLE food_security (
    region_id INT,
    security_level INT,
    CONSTRAINT fk_region FOREIGN KEY (region_id) REFERENCES regions(region_id)
);
