tables.table_schema:
CREATE TABLE WaterUsage ( 
  user_id INT PRIMARY KEY, 
  usage_date DATE, 
  water_usage_volume DECIMAL 
) ;

CREATE TABLE SanitationPractices ( 
  user_id INT PRIMARY KEY, 
  practice_date DATE, 
  practice_description TEXT 
) ;

CREATE TABLE HealthStatistics ( 
  user_id INT PRIMARY KEY, 
  health_date DATE, 
  health_condition TEXT 
) ;

CREATE TABLE UrbanSlumResidents ( 
  user_id INT PRIMARY KEY, 
  resident_name TEXT, 
  resident_address TEXT 
) ;

CREATE TABLE WaterUsageTrends (
   id INT PRIMARY KEY,
   location VARCHAR(50),
   water_source VARCHAR(50),
   usage_amount FLOAT
);

CREATE TABLE SanitationPractices (
   id INT PRIMARY KEY,
   location VARCHAR(50),
   type VARCHAR(50),
   compliance BOOLEAN
);

CREATE TABLE HealthIndicators (
   id INT PRIMARY KEY,
   location VARCHAR(50),
   indicator_name VARCHAR(50),
   value FLOAT
);

CREATE TABLE Interventions (
   id INT PRIMARY KEY,
   location VARCHAR(50),
   intervention_type VARCHAR(50),
   description TEXT
);
