tables.table_schema:
CREATE TABLE volunteer_schedule (
    volunteer_id UUID PRIMARY KEY,
    date DATE,
    shift_start TIME,
    shift_end TIME
);

CREATE TABLE task_assignment (
    task_id UUID PRIMARY KEY,
    volunteer_id UUID,
    task_description TEXT,
    assigned_date DATE
);

CREATE TABLE deployment_map (
    deployment_id UUID PRIMARY KEY,
    location TEXT,
    task_id UUID,
    assigned_volunteer UUID
);

CREATE TABLE Task (
    task_id UUID PRIMARY KEY,
    task_name VARCHAR(255) NOT NULL,
    description TEXT,
    priority INTEGER
);

CREATE TABLE Deployment (
    deployment_id UUID PRIMARY KEY,
    schedule_id UUID REFERENCES Schedule(schedule_id),
    location VARCHAR(255) NOT NULL,
    status VARCHAR(20)
);

CREATE TABLE volunteers (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  email VARCHAR(255),
  phone_number VARCHAR(20)
);

CREATE TABLE tasks (
  task_id INT PRIMARY KEY,
  task_name VARCHAR(255),
  task_description TEXT,
  task_date DATE
);
