CREATE TABLE refugee_employment (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    employment_status VARCHAR(20),
    skills VARCHAR(100)
);

CREATE TABLE refugee_employment (
    id INT,
    name VARCHAR,
    employment_status VARCHAR,
    skills VARCHAR
);

CREATE TABLE job_matching (
    id INT,
    job_title VARCHAR,
    required_skills VARCHAR,
    salary DECIMAL
);

CREATE TABLE skills (
    id UUID PRIMARY KEY,
    skill_name VARCHAR(100),
    skill_level VARCHAR(20)
);

CREATE TABLE matched_jobs (
    id UUID PRIMARY KEY,
    job_id UUID,
    applicant_id UUID,
    matching_score DECIMAL(5, 2)
);
