tables.table_schema:
CREATE TABLE Assignment (
    assignment_id INT PRIMARY KEY,
    volunteer_id INT REFERENCES VolunteerRegistration(volunteer_id),
    task_description TEXT,
    assignment_date DATE
);

CREATE TABLE volunteers (
    volunteer_id INT PRIMARY KEY,
    volunteer_name VARCHAR(50) NOT NULL,
    volunteer_email VARCHAR(50) UNIQUE NOT NULL,
    volunteer_phone VARCHAR(15) NOT NULL
);

CREATE TABLE tasks (
    task_id INT PRIMARY KEY,
    task_description VARCHAR(255) NOT NULL,
    task_due_date DATE NOT NULL,
    task_status VARCHAR(20) DEFAULT 'Pending'
);

CREATE TABLE assignments (
    assignment_id INT PRIMARY KEY,
    volunteer_id INT REFERENCES volunteers(volunteer_id),
    task_id INT REFERENCES tasks(task_id),
    assignment_date DATE NOT NULL
);

CREATE TABLE Skills (
    skill_id INT PRIMARY KEY,
    skill_name VARCHAR(50) NOT NULL UNIQUE
);
