tables.table_schema:
CREATE TABLE migration_trends (
  country_id INT,
  year INT,
  refugee_count INT
);

CREATE TABLE country_info (
  country_id INT,
  country_name VARCHAR(255),
  region VARCHAR(100),
  population INT
);

CREATE TABLE refugee_countries (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(50)
);

CREATE TABLE refugee_migration (
    migration_id INT PRIMARY KEY,
    country_id INT,
    refugee_count INT
);

CREATE TABLE migration_years (
    migration_year INT PRIMARY KEY,
    migration_id INT,
    migration_date DATE
);
